import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { useState } from 'react';

function Popup({open, setPopup, message, title, callback}) {
  const [car_name, set_car_name] = useState();
  const [users, set_users] = useState();
  const [start, set_start] = useState();
  const [end, set_end] = useState();

    const handleClose = () => {
      setPopup({open: false});
      if(callback){
        callback();
      }
    }

    return (
      <>
        <Modal show={open} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>{title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>< form  >
            <input
              type="car_name"
              placeholder="차량 이름을 입력하세요"
              onChange={(e)=>{set_car_name(e.target.value)}}
              name='car_name'
            />
            <input
              type="users"
              placeholder="사용자명을 입력하세요"
              onChange={(e)=>{set_users(e.target.value)}}
              name='users'
            />
            < input type='submit' onClick={(e)=>{handleNumSubmit(e)}}/>
          </form ></Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={handleClose}>
              OK
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
}

export default Popup;
