import Modal from 'react-bootstrap/Modal';
import { useState } from 'react';
import axios from 'axios';
import './modal.css';

function Popup({open, setPopup, message, title, callback}) {
  const [car_name, set_car_name] = useState("");
  const [users, set_users] = useState("");
  const [start, set_start] = useState("");
  //const [end, set_end] = useState("");

    const handleSubmit = (e) => {
      const event_data = {
        title: car_name,
        display: users,
        start: new Date(start),
        //end: new Date(end)
      };
      axios.post("http://localhost:1323/events",event_data,{
        'Content-Type': 'application/json',
    }, );
      }

    return (
      <div className={open ? 'openModal modal' : 'modal'}>
        <Modal className='modal'  show={open}>
          <section>
            <header>
              <Modal.Title>{title}</Modal.Title>
            </header>
            <main>
              <div>
              < form id='addEvent'>
              <select name="car_name" form="addEvent" onChange={(e)=>{set_car_name(e.target.value)}}>
                <option value="K7">K7</option>
                <option value="K9">K9</option>
            </select>
              <input
                type="string"
                placeholder="사용자명을 입력하세요"
                onChange={(e)=>{set_users(e.target.value)}}
                name='users'
              />
              <input
                type="Date"
                placeholder="사용일을 입력하세요"
                onChange={(e)=>{set_start(e.target.value)}}
                name='start'
              />
              < input type='submit' onClick={(e)=>{handleSubmit(e)}}/>
            </form >
            </div>
          </main>
          </section>
        </Modal>
        </div>
      
    );
}

export default Popup;
