import React, { useState, useEffect} from 'react';
import FullCalendar from '@fullcalendar/react' // must go before plugins
import dayGridPlugin from '@fullcalendar/daygrid' // a plugin!
import interactionPlugin from '@fullcalendar/interaction'
import axios from 'axios';

function Calendar() {
  const [event_data, set_event_data] = useState([]);
  const [event_list, set_event_list] = useState([]);

  useEffect(()=>{
    async function readEvent() {
      const url = "http://localhost:1323/events/all";
      const response = await axios.get(url);
      set_event_data(response.data.event_arr);
      console.log("标车");
      console.log(response.data);
    }
    readEvent();

    event_data.map((event) => {
      return set_event_list(e => {[...event_list, {title: event.title, display: event.display, start:event.start, end:event.end}]})
    })
  },[]);



  return (
    <FullCalendar
    plugins={[dayGridPlugin, interactionPlugin ]}
    editable
    selectable
    eventClick={handleEventClick}
    events={event_list}
  />
  )
}

function handleEventClick(info) { // bind with an arrow function
  alert('Car Name: ' + info.event.title + '\nUsers: ' + info.event.display
  + '\nstart: ' + info.event.start + '\nend: ' + info.event.end);
  // change the border color just for fun
  info.el.style.borderColor = 'red';
}

export default Calendar;
