import React, { useState, useEffect} from 'react';
import FullCalendar from '@fullcalendar/react' // must go before plugins
import dayGridPlugin from '@fullcalendar/daygrid' // a plugin!
import interactionPlugin from '@fullcalendar/interaction'
import Modal from 'Modal'
import axios from 'axios';

function Calendar() {
  const [event_data, set_event_data] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };
  const closeModal = () => {
    setModalOpen(false);
  };

  useEffect(()=>{
    async function readEvent() {
      const url = "http://localhost:1323/events/all";
      const response = await axios.get(url);
      set_event_data(response.data.event_arr);
      console.log("성공");
      console.log(response.data);
    }
    readEvent();
  },[]);

  return (
    <React.Fragment>
    <FullCalendar
    plugins={[dayGridPlugin, interactionPlugin ]}
    editable
    selectable
    eventClick={handleEventClick}
    dateClick={openModal}
    events={event_data}
  />
  <Modal open={modalOpen} close={closeModal} header="Modal heading">
        // Modal.js <main> {props.children} </main>에 내용이 입력된다. 리액트 함수형 모달
        팝업창입니다. 쉽게 만들 수 있어요. 같이 만들어봐요!
  </Modal>
  </React.Fragment>
  )
}

function handleEventClick(info) { // bind with an arrow function
  alert(info.event.title + '\n' + info.event.display);
  // change the border color just for fun
  info.el.style.backgroundColor = 'red';
}

export default Calendar;
