import Modal from 'react-bootstrap/Modal';
import { useState } from 'react';
import axios from 'axios';

function Popup({open, setPopup, message, title, callback}) {
  const [car_name, set_car_name] = useState("");
  const [users, set_users] = useState("");

    const handleSubmit = (e) => {
      const event_data = {
        title: car_name,
        display: users,
        start: new Date('09/21/2022'),
        end: new Date('09/22/2022')
      };
      axios.post("http://localhost:1323/events",event_data,{
        'Content-Type': 'application/json',
    }, );
      }

    return (
      <>
        <Modal show={open}>
          <Modal.Header closeButton>
            <Modal.Title>{title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>< form  >
            <input
              type="string"
              placeholder="차량 이름을 입력하세요"
              onChange={(e)=>{set_car_name(e.target.value)}}
              name='car_name'
            />
            <input
              type="string"
              placeholder="사용자명을 입력하세요"
              onChange={(e)=>{set_users(e.target.value)}}
              name='users'
            />
            <input
              type="Date"
              placeholder="사용시작일을 입력하세요"
              onChange={(e)=>{set_start(e.target.value)}}
              name='start'
            />
            <input
              type="Date"
              placeholder="사용종료일을 입력하세요"
              onChange={(e)=>{set_end(e.target.value)}}
              name='end'
            />
            < input type='submit' onClick={(e)=>{handleSubmit(e)}}/>
          </form ></Modal.Body>
        </Modal>
      </>
    );
}

export default Popup;
