import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { useState } from 'react';

function Popup({open, setPopup, message, title, callback}) {
  const [car_name, set_car_name] = useState();
  const [users, set_users] = useState();
  const [start, set_start] = useState();
  const [end, set_end] = useState();

    const handleSubmit = (e) => {
      e.preventDefault()
      let data={
        title: {car_name},
        display: {users},
        start: {start},
        end: {end}
      }
      fetch('/api/contact', {
        method: 'POST',
        headers: {
          'Accept': 'application/json, text/plain, */*',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      }).then((res) => {
          console.log('Response received')
          if (res.status === 200) {
              console.log('Response succeeded!')
          }
      })
    }

    return (
      <>
        <Modal show={open} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>{title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>< form  >
            <input
              type="car_name"
              placeholder="차량 이름을 입력하세요"
              onChange={(e)=>{set_car_name(e.target.value)}}
              name='car_name'
            />
            <input
              type="users"
              placeholder="사용자명을 입력하세요"
              onChange={(e)=>{set_users(e.target.value)}}
              name='users'
            />
            < input type='submit' onClick={(e)=>{handleSubmit(e)}}/>
          </form ></Modal.Body>
        </Modal>
      </>
    );
}

export default Popup;
