import Modal from 'react-bootstrap/Modal';
import { useState } from 'react';
import axios from 'axios';
import './modal.css';

function Popup({open, close, setPopup, message, title, callback}) {
  const [car_name, set_car_name] = useState("");
  const [users, set_users] = useState("");
  const [start, set_start] = useState("");
  //const [end, set_end] = useState("");
  const [all_ok, set_all_ok] = useState(false);

  const is_ok = (e) => {
    e.preventDefault()
    if (users.length === 0){
      alert("사용자명을 입력해주세요");
      return false
    }
    if (start.length === 0){
      alert("사용일을 입력해주세요");
      return false
    }
    return true;
  }

  const handleSubmit = (e) => {
      e.preventDefault()
      if (!all_ok){
        set_all_ok(is_ok(e))
      }
      if(window.confirm(`차종: ${car_name} 사용자: ${users} 사용일: ${start}가 맞는지 다시 한번 확인해주세요`)){
      const event_data = {
        title: car_name,
        display: users,
        start: new Date(start),
        //end: new Date(end)
      };
      axios.post("http://localhost:1323/events",event_data,{
        'Content-Type': 'application/json',
    }, );}

  }

    return (

      <div className={open ? 'openModal modal' : 'modal'}>
      {open ? (
        <section>
          <header>
          <Modal.Title>{title}</Modal.Title>
          </header>
          <main>< form id='addEvent'>
              <select name="car_name" form="addEvent" onChange={(e)=>{set_car_name(e.target.value)}}>
                <option value="K7">K7</option>
                <option value="K9">K9</option>
            </select>
              <input
                type="string"
                placeholder="사용자명을 입력하세요"
                onChange={(e)=>{set_users(e.target.value)}}
                name='users'
              />
              <input
                type="Date"
                placeholder="사용일을 입력하세요"
                onChange={(e)=>{set_start(e.target.value)}}
                name='start'
              />
              < input type='submit' onClick={(e)=>{handleSubmit(e)}}/>
            </form ></main>
          <footer>
            <button className="close" onClick={close}>
              close
            </button>
          </footer>
        </section>
      ) : null}
    </div>
      
    );
}

export default Popup;
